Sample.Before:
void M(IEnumerable<string> items, string s)
{
    foreach (string item in items.Where(f => f == s))
    {
    }
}

Sample.After:
void M(IEnumerable<string> items, string s)
{
    foreach (string item in items)
    {
        if (item == s)
        {
        }
    }
}

Sample.Before:
class C
{
    bool M(IEnumerable<string> items, string s)
    {
        return items.[||]Any(f => f == s);
    }
}

Sample.After:
class C
{
    bool M(IEnumerable<string> items, string s)
    {
        foreach (string f in items)
        {
            if (f == s)
            {
                return true;
            }
        }

        return false;
    }
}

